function sectionNews(){$(".section-news").length<1||document.querySelectorAll(".section-news").forEach(e=>{var r=e.querySelector(".news-slider");new Swiper(r,{slidesPerView:3,spaceBetween:40,loop:!1,speed:800,autoplay:!1,navigation:{prevEl:e.querySelector(".arrow-prev"),nextEl:e.querySelector(".arrow-next")},breakpoints:{1024:{slidesPerView:3,spaceBetween:40},768:{slidesPerView:2,spaceBetween:24},480:{slidesPerView:2,spaceBetween:24},0:{slidesPerView:1,spaceBetween:24}}})})}function hotelFilterSlider(){$(".hotel-filter").length<1||document.querySelectorAll(".hotel-filter__slider").forEach(e=>{var r=e.querySelector(".hotel-filter__slider .swiper");new Swiper(r,{slidesPerView:3,spaceBetween:0,loop:!1,speed:800,autoplay:!1,navigation:{prevEl:e.querySelector(".arrow-prev"),nextEl:e.querySelector(".arrow-next")},breakpoints:{1024:{slidesPerView:3,spaceBetween:0},768:{slidesPerView:2,spaceBetween:0},480:{slidesPerView:2,spaceBetween:0},0:{slidesPerView:1,spaceBetween:0}}})})}export{sectionNews,hotelFilterSlider};