function sectionNews(){$(".section-news").length<1||document.querySelectorAll(".section-news").forEach(e=>{var s=e.querySelector(".news-slider");new Swiper(s,{slidesPerView:3,spaceBetween:40,loop:!1,speed:800,autoplay:!1,navigation:{prevEl:e.querySelector(".arrow-prev"),nextEl:e.querySelector(".arrow-next")},breakpoints:{1024:{slidesPerView:3,spaceBetween:40},768:{slidesPerView:2,spaceBetween:24},480:{slidesPerView:2,spaceBetween:24},0:{slidesPerView:1,spaceBetween:24}}})})}export{sectionNews};