function sectionNews(){$(".section-news").length<1||document.querySelectorAll(".section-news").forEach(e=>{var r=e.querySelector(".news-slider");new Swiper(r,{slidesPerView:3,spaceBetween:24,loop:!1,speed:800,autoplay:!1,navigation:{prevEl:e.querySelector(".arrow-prev"),nextEl:e.querySelector(".arrow-next")},breakpoints:{1024:{slidesPerView:3,spaceBetween:24},768:{slidesPerView:2,spaceBetween:24},480:{slidesPerView:2,spaceBetween:24},0:{slidesPerView:1,spaceBetween:24}}})})}function sliderWithShadow(){$(".slider-with-shadow").length<1||document.querySelectorAll(".slider-with-shadow").forEach(e=>{var r=e.querySelector(".slider-with-shadow .swiper");new Swiper(r,{slidesPerView:3,spaceBetween:0,loop:!1,speed:800,autoplay:!1,navigation:{prevEl:e.querySelector(".arrow-prev"),nextEl:e.querySelector(".arrow-next")},breakpoints:{1024:{slidesPerView:3,spaceBetween:0},768:{slidesPerView:2,spaceBetween:0},480:{slidesPerView:2,spaceBetween:0},0:{slidesPerView:1,spaceBetween:0}}})})}export{sectionNews,sliderWithShadow};