function createFilterTab(){document.querySelectorAll(".filter-section").forEach(l=>{let r=l.nextElementSibling;r?.classList.contains("filter-section-result")&&l.querySelectorAll(".filter-button[data-type]").forEach(e=>{e.addEventListener("click",function(){l.querySelectorAll(".filter-button").forEach(e=>e.classList.remove("active")),this.classList.add("active");let t=this.dataset.type,e=r.querySelectorAll(".filter-item");ScrollTrigger.getAll().forEach(e=>e.disable()),gsap.timeline().to(r,{autoAlpha:0,duration:.3}).call(()=>{e.forEach(e=>{e.style.display="all"===t||e.dataset.filter===t?"":"none"})}).to(r,{autoAlpha:1,duration:.3}).call(()=>{ScrollTrigger.getAll().forEach(e=>e.enable()),ScrollTrigger.refresh()})})})})}export{createFilterTab};